/**
 * @openapi
 *  tags:
 *    name: Review
 *    description: API to handle book review-related operations
 */

/**
 * @openapi
 * components:
 *   schemas:
 *     Review:
 *       type: object
 *       required:
 *         - bookId
 *         - userId
 *         - text
 *         - rating
 *       properties:
 *         id:
 *           type: string
 *           description: The auto-generated id of the review
 *         bookId:
 *           type: string
 *           description: The id of the book that the review is about
 *         userId:
 *           type: string
 *           description: The id of the user that wrote the review
 *         text:
 *           type: string
 *           description: The text of the review
 *         rating:
 *           type: number
 *           description: The rating of the review
 *         date:
 *           type: string
 *           description: The date of the review
 *         likes:
 *           type: array
 *           items:
 *             type: object
 *             properties:
 *               userId:
 *                 type: string
 *                 description: The id of the user that liked the review
 *           description: The likes of the review
 *         comments:
 *           type: array
 *           items:
 *             type: object
 *             properties:
 *               commentId:
 *                 type: string
 *                 description: The id of the comment
 *           description: The comments of the review
 *       example:
 *         bookId: 123e4567-e89b-12d3-a456-426614174000
 *         userId: 123e4567-e89b-12d3-a456-426614174000
 *         text: Great book!
 *         rating: 5
 *         date: 2022-01-01
 *         likes: []
 *         comments: []
 */

/**
 * @openapi
 * components:
 *   schemas:
 *     Comment:
 *       type: object
 *       required:
 *         - reviewId
 *         - createdBy
 *         - text
 *       properties:
 *         _id:
 *           type: string
 *           description: The id of the comment (autogenerated)
 *         reviewId:
 *           type: string
 *           description: The id of the review
 *         createdBy:
 *           type: string
 *           description: The id of the user that created the comment
 *         text:
 *           type: string
 *           description: The text of the comment
 *         createdAt:
 *           type: string
 *           description: The date of the comment
 *       example:
 *         _id: 123e4567-e89b-12d3-a456-426614174000
 *         reviewId: 123e4567-e89b-12d3-a456-426614174000
 *         createdBy: 123e4567-e89b-12d3-a456-426614174000
 *         text: Great book!
 *         createdAt: 2022-01-01
 */

